import "core-js/modules/es.array.push.js";
import { ref, onBeforeUnmount } from 'vue';
//import { useRoute } from 'vue-router';
import { doc, getDoc, onSnapshot, addDoc, updateDoc, arrayUnion, collection, serverTimestamp, useRoute } from 'firebase/firestore';
import { db } from '../firebase';
const chatId = "6HaLRtFkMgZvhMIVC2PQ";
export default {
  __name: 'ChatPage',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    //:class="msg.sender === currentUser?.uid ? 'sent' : 'received'"
    let newMessage = "";
    const route = useRoute();
    const user1 = route.params.user1;
    const user2 = route.params.user2;
    const auth = getAuth();
    const currentUser = auth.currentUser;
    const messageList = ref([]);
    let chatUnsub = null;
    async function loadMessagesByIds(ids) {
      const messages = [];
      for (const id of ids) {
        const messageDoc = await getDoc(doc(db, 'messages', id));
        if (messageDoc.exists()) {
          messages.push({
            id: messageDoc.id,
            ...messageDoc.data()
          });
        }
      }
      messageList.value = messages;
    }

    //const chatId = user1 < user2 ? `${user1}_${user2}` : `${user2}_${user1}`; 
    const chatRef = doc(db, 'chats', chatId);
    chatUnsub = onSnapshot(chatRef, chatSnap => {
      if (chatSnap.exists()) {
        const chatData = chatSnap.data();
        const messageIds = chatData.messages || [];
        loadMessagesByIds(messageIds);
      } else {
        console.log('Chat does not exist.');
        messageList.value = [];
      }
    });
    async function sendMessage() {
      if (!newMessage.trim()) return;
      try {
        // 1. Add message to messages collection
        const messageData = {
          sender: user1,
          receiver: user2,
          message: newMessage,
          timestamp: serverTimestamp()
        };
        const newMsgRef = await addDoc(collection(db, 'messages'), messageData);

        // 2. Update the chat document to include this message ID
        await updateDoc(doc(db, 'chats', chatId), {
          messages: arrayUnion(newMsgRef.id)
        });

        // 3. Clear the input field
        newMessage = "";
      } catch (error) {
        console.error("Error sending message:", error);
      }
    }
    function formatTimestamp(ts) {
      if (!ts) return '';
      const date = ts.toDate(); // converts Firestore Timestamp to JS Date
      return date.toLocaleTimeString([], {
        hour: '2-digit',
        minute: '2-digit'
      });
    }
    onBeforeUnmount(() => {
      if (chatUnsub) chatUnsub();
    });
    const __returned__ = {
      get newMessage() {
        return newMessage;
      },
      set newMessage(v) {
        newMessage = v;
      },
      route,
      user1,
      user2,
      auth,
      currentUser,
      messageList,
      get chatUnsub() {
        return chatUnsub;
      },
      set chatUnsub(v) {
        chatUnsub = v;
      },
      loadMessagesByIds,
      chatId,
      chatRef,
      sendMessage,
      formatTimestamp,
      ref,
      onBeforeUnmount,
      get doc() {
        return doc;
      },
      get getDoc() {
        return getDoc;
      },
      get onSnapshot() {
        return onSnapshot;
      },
      get addDoc() {
        return addDoc;
      },
      get updateDoc() {
        return updateDoc;
      },
      get arrayUnion() {
        return arrayUnion;
      },
      get collection() {
        return collection;
      },
      get serverTimestamp() {
        return serverTimestamp;
      },
      get useRoute() {
        return useRoute;
      },
      get db() {
        return db;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
