      get onSnapshot() {
        return onSnapshot;
      },
      get addDoc() {
        return addDoc;
      },
      get updateDoc() {
        return updateDoc;
      },
      get arrayUnion() {
        return arrayUnion;
      },
      get collection() {
        return collection;
      },
      get serverTimestamp() {
        return serverTimestamp;
