        return unsubscribeTyping;
      },
      set unsubscribeTyping(v) {
        unsubscribeTyping = v;
      },
      goBackToChatList,
      setupPresence,
      handleTyping,
      isImageMessage,
      formatTimestamp,
      loadMessagesByIds,
      sendCombinedMessage,
